css:
.id-card {
    display: flex;
    justify-content: space-between;
}

.front, .back {
    width: 50%; 
    text-align: center;
}
.flex-item {
    width: 50%;
    padding: 10px; 
    box-sizing: border-box; 

}
.image {
    width: 300px;
    height: 137px;
}

.card-content h2 {
    font-size: 18px;
    margin-bottom: 5px;
}

.flex-container {
    display: flex;
    align-items: center;
    justify-content: center;

    }
.logo {
    top: 0;
    left: 0;
    width: 100%;
    height: 3.2cm;
    text-align: center;
    /*line-height: 5.2cm;*/
}
.center {
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #D22B2B;
  height: 38px;
}
  .footer {
    bottom: 0;
    left: 0;
    width: 100%;
    height: 1cm;
    text-align: center;
    line-height: 1cm;
    }

.content {
    align-items: center;
    justify-content: center;
    font-weight:bold;
    font-size:10px;
    /*padding-top:20px;*/

}
