css:
.id-card {
    display: flex;
    justify-content: space-between;
    height:100%;
}

.front, .back {
    width: 50%; 
    text-align: center;
}
.flex-item {
    width: 50%;
    padding: 10px; 
    box-sizing: border-box; 

}
.image {
    width: 150px;
    height: 137px;
}

.card-content h2 {
    font-size: 18px;
    margin-bottom: 5px;


}

.background{
    background-image: url(/files/watermark-01.png);
    background-position:center;
    filter:alpha(opacity=60);
    background-size: cover;'
}

.flex-container {
    display: flex;
    align-items: center;
    justify-content: center;

    }
.logo-flex-container {
    display: flex;
    align-items: center;
    justify-content: center;

    }
.logo {
    top: 0;
    left: 0;
    width: 100%;
    height: 3.2cm;
    text-align: center;
    /*line-height: 5.2cm;*/
}
.center {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 50px;
  font-size:15px;

}
  .footer {
    bottom: 0;
    left: 0;
    width: 100%;
    height: 1cm;
    text-align: center;
    line-height: 1cm;
    }

.content {
    align-items: center;
    justify-content: center;
    font-weight:bold;
    font-size:10px;
    line-height: 1.4;
    padding: 2px;

    /*padding-top:20px;*/

}
